function App() {
  const [email, setUsername] = useState('');
  const [passwd, setpasswd] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, passwd }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          // Si la respuesta es exitosa, puedes realizar alguna acción, como redirigir al usuario a otra pantalla.
          setMessage('Inicio de sesión exitoso');
        } else {
          setMessage('Credenciales incorrectas');
        }
      } else {
        setMessage('Error en la solicitud');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    }
  };

// Verifica si los campos de usuario y contraseña están completos
const isFormValid = email.trim() !== '' && passwd.trim() !== '';

return (
  <div>
    <h1>Iniciar sesión</h1>
    <div>
      <input
        type="email"
        placeholder="Username"
        value={email}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
    </div>
    <div>
      <input
        type="passwd"
        placeholder="Contraseña"
        value={passwd}
        onChange={(e) => setpasswd(e.target.value)}
        required
      />
    </div>
    <button onClick={handleLogin} disabled={!isFormValid}>
      Iniciar sesión
    </button>
    <p>{message}</p>
  </div>
);
}

export default App;
