// LoginForm.js
import React from 'react';

function LoginForm({ email, passwd, setpasswd, message, handleLogin, isFormValid }) {
  const setUsername = (value) => {
    // Aquí deberías hacer algo con el valor del email si es necesario.
  };


  return (
    <div className="contenedor">
      <h1>Iniciar sesión</h1>
      <div>
        <input
          type="email"
          placeholder="Username"
          value={email}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="Contraseña"
          value={passwd}
          onChange={(e) => setpasswd(e.target.value)}
          required
        />
      </div>
      <div>
        <button onClick={handleLogin} disabled={!isFormValid}>
          Iniciar sesión
        </button>
        <p>{message}</p>
      </div>
    </div>
  );
}

export default LoginForm;
