// LoginForm.js
import React, { useState } from 'react';

function LoginForm({ setShowLoginForm }) {
  const [email, setEmail] = useState('');
  const [passwd, setPasswd] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    // Realiza la lógica de inicio de sesión aquí

    // Si el inicio de sesión es exitoso, puedes usar setShowLoginForm para volver a la pantalla de inicio.
    setShowLoginForm(false);
  };

  const isFormValid = email.trim() !== '' && passwd.trim() !== '';

  return (
    <div className="contenedor">
      <h1>Iniciar sesión</h1>
      <div>
        <input
          type="email"
          placeholder="Username"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="Contraseña"
          value={passwd}
          onChange={(e) => setPasswd(e.target.value)}
          required
        />
      </div>
      <div>
        <button onClick={handleLogin} disabled={!isFormValid}>
          Iniciar sesión
        </button>
        <p>{message}</p>
      </div>
    </div>
  );
}

export default LoginForm;
