import React, { useEffect, useState } from 'react';

function VerTorneos() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/torneo/ver')
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener datos:', error);
        setIsLoading(false);
      });
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredData = data.nombres.filter((nombre) =>
    nombre.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="contenedor">
      <h2>Lista de Torneos</h2>
      <input
        type="text"
        placeholder="Buscar por nombre del torneo..."
        value={searchTerm}
        onChange={handleSearch}
      />
      {isLoading ? (
        <p>Cargando...</p>
      ) : (
        filteredData.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Inicio</th>
                <th>Fin</th>
                <th>Reglas</th>
                <th>Consola</th>
                <th>Participantes</th>
                <th>Juego</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {filteredData.map((nombre, index) => (
                <tr key={index}>
                  <td>{nombre}</td>
                  <td>{data.inicio[index]}</td>
                  <td>{data.fin[index]}</td>
                  <td>{data.reglas[index]}</td>
                  <td>{data.consola[index]}</td>
                  <td>{data.participante[index]}</td>
                  <td>{data.juego[index]}</td>
                  <td>{data.status[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No hay datos disponibles.</p>
        )
      )}
    </div>
  );
}

export default VerTorneos;
