import React, { useEffect, useState } from 'react';

function VerTorneos() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/torneo/ver')  // Reemplaza con la URL de tu endpoint de Flask
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener datos:', error);
        setIsLoading(false);
      });
  }, []);

  return (
    <div className="contenedor">
      <h2>Lista de Torneos</h2>
      {isLoading ? (
        <p>Cargando...</p>
      ) : (
        data.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Inicio</th>
                <th>Fin</th>
                <th>Reglas</th>
                <th>Consola</th>
                <th># Participantes</th>
                <th>Juego</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {data.map((torneo, index) => (
                <tr key={index}>
                  <td>{torneo.nombre}</td>
                  <td>{torneo.inicio}</td>
                  <td>{torneo.fin}</td>
                  <td>{torneo.reglas}</td>
                  <td>{torneo.consola}</td>
                  <td>{torneo.participante}</td>
                  <td>{torneo.juego}</td>
                  <td>{torneo.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No hay datos disponibles.</p>
        )
      )}
    </div>
  );
}

export default VerTorneos;
