import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';

function EditarPerfil() {
  const [contraseña, setContraseña] = useState('');
  const [nuevoNombre, setNuevoNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/');
  };

  const handleEditar = async () => {
    try {
        const authToken = localStorage.getItem('authToken');
      const response = await fetch('http://localhost:5000/participante/editar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            authToken,
            nuevo_nombre: nuevoNombre,
            nueva_contraseña: contraseña,
            nuevo_correo: correo,      
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log(data.message);
        // Puedes manejar la respuesta según tus necesidades
      } else {
        const errorData = await response.json();
        console.error(errorData.error);
        // Puedes manejar el error según tus necesidades
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      // Puedes manejar el error de la solicitud según tus necesidades
    }
  };

  return (
    <div className="contenedor">
      <div className='home'>
       <nav>
            <Link to="/registrar-torneo">Torneos</Link>
            <Link to="/ver-perfil">Perfil</Link>
            <Link to="/editar-perfil">Editar Perfil</Link>
             <button onClick={handleLogout}>Cerrar Sesión</button>
        </nav>
       </div>
      <p></p>
      <div>
        <input
          type="text"
          placeholder="Nuevo nombre"
          value={nuevoNombre}
          onChange={(e) => setNuevoNombre(e.target.value)}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Nuevo correo"
          value={correo}
          onChange={(e) => setCorreo(e.target.value)}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Nueva contraseña"
          value={contraseña}
          onChange={(e) => setContraseña(e.target.value)}
          required
        />
      </div>
      <p></p>
      <button onClick={handleEditar}>Editar Perfil</button>
    </div>
  );
}

export default EditarPerfil;
