import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CrearTorneo({ onAuthentication }) {
  const navigate = useNavigate();
  const [torneoData, setTorneoData] = useState({
    nombre: '',
    fecha_inicio: '',
    fecha_fin: '',
    reglas: '',
    consola: '',
    num_participantes: '',
    juego: '',
    status: '',
    // Agrega los atributos adicionales aquí
    idTorneo: '',
    imagen: '', // Por ejemplo, agrego idTorneo e imagen como ejemplos
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTorneoData({ ...torneoData, [name]: value });
  };

  const handleCrearTorneo = async () => {
    try {
      const response = await fetch('http://localhost:5000/torneo/crear', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(torneoData),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          navigate('/home');
        } else {
          setMessage(data.message);
        }
      } else {
        setMessage('Error al crear el torneo');
      }
    } catch (error) {
      setMessage('Error en la solicitud');
    }
  };

  const isFormValid =
    Object.values(torneoData).every((value) => value.trim() !== '');

  return (
    <div className="contenedor">
      <h1>Crear Torneo</h1>
      <div>
        <input
          type="text"
          placeholder="Nombre del torneo"
          name="nombre"
          value={torneoData.nombre}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Estatus"
          name="status"
          value={torneoData.status}
          onChange={handleChange}
          required
        />
      </div>
      {/* Agrega más campos aquí */}
      <div>
        <p></p>
        <button type="submit" onClick={handleCrearTorneo} disabled={!isFormValid}>
          Crear Torneo
        </button>
        <p>{message}</p>
      </div>
    </div>
  );
}

export default CrearTorneo;
