import React, { useState } from 'react';

function BuscarParticipante() {
  const [nombre, setNombre] = useState('');
  const [participantes, setParticipantes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async () => {
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/participante/buscar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre }),
      });

      if (response.ok) {
        const data = await response.json();
        setParticipantes(data);
      } else {
        console.error('Error al buscar el participante');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="contenedor">
        <input className="redondo"
          type="text"
          placeholder="Nombre del participante"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
        />
      <p></p>
      <button onClick={handleSearch} disabled={isLoading}>
        Buscar Participante
      </button>

      {isLoading && <p>Cargando...</p>}

      {participantes.nombres && participantes.nombres.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Inicio</th>
              <th>Fin</th>
              <th>Reglas</th>
              <th>Consola</th>
              <th>Participantes</th>
              <th>Juego</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {participantes.nombres.map((nombre, index) => (
              <tr key={index}>
                <td>{nombre}</td>
                <td>{participantes.inicio[index]}</td>
                <td>{participantes.fin[index]}</td>
                <td>{participantes.reglas[index]}</td>
                <td>{participantes.consola[index]}</td>
                <td>{participantes.participante[index]}</td>
                <td>{participantes.juego[index]}</td>
                <td>{participantes.status[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {!isLoading && participantes.nombres && participantes.nombres.length === 0 && (
        <p>No se encontraron participantes.</p>
      )}
    </div>
  );
}

export default BuscarParticipante;
