import React, { useEffect, useState } from 'react';

function VerTorneos() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/torneo/ver')  // Reemplaza con la URL de tu endpoint de Flask
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener datos:', error);
        setIsLoading(false);
      });
  }, []);

  return (
    <div className="contenedor">
      <h2>Lista de Participantes</h2>
      {isLoading ? (
        <p>Cargando...</p>
      ) : (
        data.nombres && data.inicio && data.fin && data.reglas && data.consola && data.participante && data.juego && data.status && data.nombres.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Email</th>
              </tr>
            </thead>
            <tbody>
              {data.nombres.map((nombre, index) => (
                <tr key={index}>
                  <td>{nombre}</td>
                  <td>{data.inicio[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No hay datos disponibles.</p>
        )
      )}
    </div>
  );
}

export default VerTorneos;
