import React, { useEffect, useState } from 'react';

function VerTorneos() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [nombre, setNombre] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/torneo/ver')  
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener datos:', error);
        setIsLoading(false);
      });
  }, []);

  const BuscarTorneo = async () => {
    try {
      const response = await fetch('http://localhost:5000/torneo/buscar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
            setMessage("Torneo registrado exitosamente");
        } else {
          setMessage(data.message);
        }
      } 
    else{
        setMessage("Error al registrar, el usuario ya existe")
    }
    } catch (error) {setMessage('Error en la solicitud');}
  };

  const isFormValid = nombre.trim() !== ''

  return (
    <div className="contenedor">
      <h2>Lista de Torneo</h2>

      <input className = "redondo"
        type="text"
        placeholder="ParticipantesBuscar por nombre del torneo"
        value={nombre}
        onChange={(e) => setNombre(e.target.value)}
        />
      <button type="submit" onClick={BuscarTorneo} disabled={!isFormValid}>Buscar</button>

      {isLoading ? (
        <p>Cargando...</p>
      ) : (
        data.nombres && data.inicio && data.fin && data.reglas && data.consola && data.participante && data.juego && data.status && data.nombres.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Inicio</th>
                <th>Fin</th>
                <th>Reglas</th>
                <th>Consola</th>
                <th>Participantes</th>
                <th>Juego</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {data.nombres.map((nombre, index) => (
                <tr key={index}>
                  <td>{nombre}</td>
                  <td>{data.inicio[index]}</td>
                  <td>{data.fin[index]}</td>
                  <td>{data.reglas[index]}</td>
                  <td>{data.consola[index]}</td>
                  <td>{data.participante[index]}</td>
                  <td>{data.juego[index]}</td>
                  <td>{data.status[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No hay datos disponibles.</p>
        )
      )}
    </div>
  );
}

export default VerTorneos;
