import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CrearTorneo({ onAuthentication }) {
  const navigate = useNavigate();

  const [nombre, setNombre] = useState('');
  const [fecha_inicio, setInicio] = useState('');
  const [fecha_fin, setFin] = useState('');
  const [reglas, setReglas] = useState('');
  const [consola, setConsola] = useState('');
  const [num_participantes, setParticipantes] = useState('');
  const [juego, setJuego] = useState('');
  const [status, setStatus] = useState('');

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTorneoData({ ...torneoData, [name]: value });
  };

  const handleCrearTorneo = async () => {
    try {
      const response = await fetch('http://localhost:5000/torneo/registrar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(torneoData),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          navigate('/home');
        } else {
          setMessage(data.message);
        }
      } else {
        setMessage('Error al crear el torneo');
      }
    } catch (error) {
      setMessage('Error en la solicitud');
    }
  };

  const isFormValid =
    Object.values(torneoData).every((value) => value.trim() !== '');

  return (
    <div className="contenedor">
      <h1>Crear Torneo</h1>
      <div>
        <input
          type="text"
          placeholder="Nombre del torneo"
          name="nombre"
          value={torneoData.nombre}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Estatus"
          name="status"
          value={torneoData.status}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Consola"
          name="consola"
          value={torneoData.consola}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Fecha de inicio"
          name="fecha_inicio"
          value={torneoData.fecha_inicio}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Número de participantes"
          name="num_participantes"
          value={torneoData.num_participantes}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Fecha de término"
          name="fecha_fin"
          value={torneoData.fecha_fin}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Juego"
          name="juego"
          value={torneoData.juego}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Reglas"
          name="reglas"
          value={torneoData.reglas}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <p></p>
        <button type="submit" onClick={handleCrearTorneo} disabled={!isFormValid}>
          Crear Torneo
        </button>
        <p>{message}</p>
      </div>
    </div>
  );
}

export default CrearTorneo;
