import React, { useEffect, useState } from 'react';

function AdminTabla() {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/admin/ver')  // Reemplaza con la URL de tu endpoint de Flask
      .then((response) => response.json())
      .then((data) => setClientes(data))  // Suponiendo que los datos son un arreglo de clientes
      .catch((error) => console.error('Error al obtener los datos:', error));
  }, []);

  if (!Array.isArray(clientes)) {
    return <div>Cargando...</div>;
  }

  return (
    <div className="contenedor">
      <h2>Lista de Administradores</h2>
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {clientes.map((cliente) => (
            <tr key={cliente.idAdministrador}>
              <td>{cliente.nombre}</td>
              <td>{cliente.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminTabla;
