import React, { useState } from 'react';

const EliminarTorneo = () => {
  const [nombreTorneo, setNombreTorneo] = useState('');
  const [message, setMessage] = useState('');

  const handleEliminar = async () => {
    try {
      const response = await fetch('http://localhost:5000/torneo/eliminar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre: nombreTorneo }),
      });

      if (response.ok) {
        // Manejar la respuesta si es necesario
        setMessage('Torneo eliminado exitosamente');
      } else {
        // Manejar errores
        setMessage('Error al eliminar el torneo');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      setMessage('Error en la solicitud');
    }
  };

  return (
    <div className="contenedor">
      <h2>Eliminar Torneo</h2>
      <div>
        <label>Nombre del Torneo:</label>
        <input
          type="text"
          value={nombreTorneo}
          onChange={(e) => setNombreTorneo(e.target.value)}
        />
      </div>
      <div>
        <button onClick={handleEliminar}>Eliminar Torneo</button>
      </div>
      <p>{message}</p>
    </div>
  );
};

export default EliminarTorneo;
