import React, { useState } from 'react';

function EditarTorneo() {
  const [nombre, setNombre] = useState('');
  const [nuevoNombre, setNuevoNombre] = useState('');
  // Agrega más estados según tus necesidades

  const handleEditar = async () => {
    try {
      const response = await fetch('http://localhost:5000/torneo/editar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          nombre,
          nuevo_nombre: nuevoNombre,
          // Agrega más campos según tus necesidades
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log(data.message);
        // Puedes manejar la respuesta según tus necesidades
      } else {
        const errorData = await response.json();
        console.error(errorData.error);
        // Puedes manejar el error según tus necesidades
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      // Puedes manejar el error de la solicitud según tus necesidades
    }
  };

  return (
    <div>
      <h2>Editar Torneo</h2>
      <label>Nombre del Torneo:</label>
      <input
        type="text"
        value={nombre}
        onChange={(e) => setNombre(e.target.value)}
      />
      <label>Nuevo Nombre:</label>
      <input
        type="text"
        value={nuevoNombre}
        onChange={(e) => setNuevoNombre(e.target.value)}
      />
      {/* Agrega más campos según tus necesidades */}
      <button onClick={handleEditar}>Editar Torneo</button>
    </div>
  );
}

export default EditarTorneo;
