import './App.css';
import { useState, useEffect } from 'react';
import React from 'react';
import LoginForm from './components/LoginForm'
import Home from './components/Home'
import Editar from './components/Editar'
import RegistrationForm from './components/Registrar'
import AdminTabla from './components/Ver-Admin'
import RegistrationAdmin from './components/Registrar-Admin'
import CrearTorneo from './components/Crear-Torneo'
import VerParticipantes from './components/Ver-Participantes'
import VerTorneos from './components/Ver-Torneos'
import BuscarTorneo from './components/Buscar-Torneo'
import Home_Admin from './components/Home-Admin';
import Home_Super from './components/Home-Super';
import BuscarAdmin from './components/Buscar-Admin';
import RegistrarTorneo from './components/Registrar-Torneo';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import BuscarParticipante from './components/Buscar-Participante';

function App() {

  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Función para establecer el estado de autenticación
  const handleAuthentication = (authenticated) => {
    setIsAuthenticated(authenticated);
  };

  useEffect(() => {
    // Verificar si hay un token almacenado en el localStorage al cargar la página
    const token = localStorage.getItem('authToken');
    if (token) {
      // Puedes verificar la validez del token aquí si es necesario
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<LoginForm onAuthentication={handleAuthentication} />}
        />
        <Route
          path="/home"
          element={isAuthenticated ? <Home_Admin/> : <p>Debes iniciar sesión</p>}
        />
        <Route
          path="/editar-perfil"
          element={isAuthenticated ? <Editar/> : <p>Debes iniciar sesión</p>}
        />
        <Route
          path="/registrar"
          element={<RegistrationForm onAuthentication={handleAuthentication} />}
        />
        <Route
          path="/registrar-admin"
          element={<RegistrationAdmin onAuthentication={handleAuthentication} />}
        />
        <Route
          path="/ver-admin"
          element={<AdminTabla/>}
        />
        <Route
          path="/crear-torneo"
          element={<CrearTorneo/>}
        />
        <Route
          path="/ver-torneo"
          element={<VerTorneos/>}
        />
        <Route
          path="/ver-participante"
          element={<VerParticipantes/>}
        />
        <Route
          path="/buscar-torneo"
          element={<BuscarTorneo/>}
        />
        <Route
          path="/buscar-participante"
          element={<BuscarParticipante/>}
        />
        <Route
          path="/buscar-admin"
          element={<BuscarAdmin/>}
        />
        <Route
          path="/registrar-torneo"
          element={<RegistrarTorneo/>}
        />
      </Routes>
    </Router>
  );
}

export default App;
