import React, { useEffect, useState } from 'react';
import {  Link, useNavigate } from 'react-router-dom';

function AgregarAmigo() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [nombre, setNombre] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/participante/ver')
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener datos:', error);
        setIsLoading(false);
      });
  }, []);

  const isFormValid = nombre.trim() !== '';

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/');
  };

  const handleAgregar = async () => {
    try {
        const id1 = localStorage.getItem('authToken');
      const response = await fetch('http://localhost:5000/amigo/registrar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password, nombre }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
            localStorage.setItem('authToken', data.token);
            localStorage.setItem('userRole', data.token);
            onAuthentication(true);
            navigate('/home-participante');
        } else {
          setMessage(data.message);
        }
      } 
    else{
        setMessage("Error al registrar, el usuario ya existe")
    }
    } catch (error) {setMessage('Error en la solicitud');}
  };

  const handleSearch = async () => {
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/participante/buscar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre }),
      });

      if (response.ok) {
        const searchData = await response.json();
        setData(searchData);
      } else {
        console.error('Error al buscar el participante');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="contenedor">
      <div className='home'>
       <nav>
            <Link to="/registrar-torneo">Torneos</Link>
              <Link to="/ver-perfil">Perfil</Link>
              <Link to="/editar-perfil">Editar Perfil</Link>
              <Link to="/agregar-amigo">Agregar amigo</Link>
             <button onClick={handleLogout}>Cerrar Sesión</button>
        </nav>
       </div>
       <p></p>
      <div>
        <input
          className="redondo"
          type="text"
          placeholder="Buscar por nombre del participante"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
        />
        <button onClick={handleSearch} disabled={!isFormValid}>
          Buscar Participante
        </button>
      </div>

      {isLoading ? (
        <p>Cargando...</p>
      ) : (
        data.nombres && data.correos && data.nombres.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Nombre</th>
                <th>Email</th>
                <th>Agregar</th>
              </tr>
            </thead>
            <tbody>
              {data.nombres.map((nombre, index) => (
                <tr key={index}>
                  <td>{data.username[index]}</td>
                  <td>{nombre}</td>
                  <td>{data.correos[index]}</td>
                  <td>
                    <button onClick={() => handleAgregar(nombre)}>
                      Agregar
                    </button>
                </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No hay datos disponibles.</p>
        )
      )}
    </div>
  );
}

export default AgregarAmigo;
