import React, { useEffect, useState } from 'react';

function AdminTabla() {
  const [administradores, setAdministradores] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/admin/ver')  // Reemplaza con la URL de tu endpoint de Flask
      .then((response) => response.json())
      .then((data) => {
        setAdministradores(data.administradores);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener administradores:', error);
        setIsLoading(false);
      });
  }, []);

  return (
    <div className="contenedor">
      <h2>Lista de Administradores</h2>
      {isLoading ? (
        <p>Cargando...</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {administradores.map((admin) => (
              <tr key={admin.idAdministrador}>
                <td>{admin.nombre}</td>
                <td>{admin.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AdminTabla;
