import React, { useState } from 'react';

function BuscarTorneo() {
  const [nombre, setNombre] = useState('');
  const [torneos, setTorneos] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async () => {
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/torneo/buscar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre }),
      });

      if (response.ok) {
        const data = await response.json();
        setTorneos(data);
      } else {
        console.error('Error al buscar el torneo');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <label>
        Nombre del Torneo:
        <input
          type="text"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
        />
      </label>
      <button onClick={handleSearch} disabled={isLoading}>
        Buscar Torneo
      </button>

      {isLoading && <p>Cargando...</p>}

      {torneos.nombres && torneos.nombres.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Inicio</th>
              <th>Fin</th>
              <th>Reglas</th>
              <th>Consola</th>
              <th>Participantes</th>
              <th>Juego</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {torneos.nombres.map((nombre, index) => (
              <tr key={index}>
                <td>{nombre}</td>
                <td>{torneos.inicio[index]}</td>
                <td>{torneos.fin[index]}</td>
                <td>{torneos.reglas[index]}</td>
                <td>{torneos.consola[index]}</td>
                <td>{torneos.participante[index]}</td>
                <td>{torneos.juego[index]}</td>
                <td>{torneos.status[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {!isLoading && torneos.nombres && torneos.nombres.length === 0 && (
        <p>No se encontraron torneos.</p>
      )}
    </div>
  );
}

export default BuscarTorneo;
