function App() {
  const [email, setUsername] = useState('');
  const [passwd, setpasswd] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, passwd }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          // Si la respuesta es exitosa, puedes realizar alguna acción, como redirigir al usuario a otra pantalla.
          setMessage('Inicio de sesión exitoso');
        } else {
          setMessage(data.message);
        }
      } else {
        setMessage('Error en la solicitud');
      }
    } catch (error) {}
  };

// Verifica si los campos de usuario y contraseña están completos
const isFormValid = email.trim() !== '' && passwd.trim() !== '';

const [showLoginForm, setShowLoginForm] = useState(false);

return (
  <div className="app-container">
    {!showLoginForm ? (
      <div>
        <h1>Bienvenido</h1>
        <button onClick={() => setShowLoginForm(true)}>Iniciar sesión</button>
      </div>
    ) : (
      <LoginForm setShowLoginForm={setShowLoginForm} />
    )}
  </div>
);
}

export default App;
