import React, { useState } from 'react';

function BuscarAdmin() {
  const [nombre, setNombre] = useState('');
  const [administradors, setAdmins] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async () => {
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/admin/buscar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre }),
      });

      if (response.ok) {
        const data = await response.json();
        setAdmins(data);
      } else {
        console.error('Error al buscar el administrador');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="contenedor">
        <input className="redondo"
          type="text"
          placeholder="Nombre del administrador"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
        />
      <p></p>
      <button onClick={handleSearch} disabled={isLoading}>
        Buscar Admin
      </button>

      {isLoading && <p>Cargando...</p>}

      {administradors.nombres && administradors.nombres.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Correo</th>
            </tr>
          </thead>
          <tbody>
            {administradors.nombres.map((nombre, index) => (
              <tr key={index}>
                <td>{nombre}</td>
                <td>{administradors.correos[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {!isLoading && administradors.nombres && administradors.nombres.length === 0 && (
        <p>No se encontraron administradors.</p>
      )}
    </div>
  );
}

export default BuscarAdmin;
