import React, { useEffect, useState } from 'react';

function VerTorneos() {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/torneo/ver')
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener datos:', error);
        setIsLoading(false);
      });
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  // Ensure data.nombres exists before filtering
  const filteredData =
    data.nombres && data.nombres.length > 0
      ? data.nombres.filter((nombre) =>
          nombre.toLowerCase().includes(searchTerm.toLowerCase())
        )
      : [];

  return (
    <div className="contenedor">
      <h2>Lista de Torneos</h2>
      <input
        type="text"
        placeholder="Buscar por nombre del torneo..."
        value={searchTerm}
        onChange={handleSearch}
      />
      {isLoading ? (
        <p>Cargando...</p>
      ) : (
        filteredData.length > 0 ? (
          <table>
            {/* ... (rest of your component remains unchanged) */}
          </table>
        ) : (
          <p>No hay datos disponibles.</p>
        )
      )}
    </div>
  );
}

export default VerTorneos;
