// En tu componente VerPerfil.js

import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function VerPerfil() {
  const [data, setData] = useState([]);
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [nombre, setNombre] = useState('');

  const verPerfil = async () => {
    try {
      const authToken = localStorage.getItem('authToken');

      const response = await fetch('http://localhost:5000/participante/ver-perfil', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ authToken }),
      });

      if (response.ok) {
        useEffect(() => {
            fetch('http://localhost:5000/admin/ver')
              .then((response) => response.json())
              .then((data) => {
                setData(data);
                setIsLoading(false);
              })
              .catch((error) => {
                console.error('Error al obtener datos:', error);
                setIsLoading(false);
              });
          }, []);

      }}
    catch (error) {
      console.error('Error en la solicitud:');
    }
  };

  useEffect(() => {
    // Llamada a tu endpoint original
    fetch('http://localhost:5000/admin/ver')
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener datos:', error);
        setIsLoading(false);
      });
  }, []);

  // Resto del componente...

  return (
    <div className="contenedor">
      {/* ... Otro código ... */}

      <div>
        {/* ... Otro código ... */}
        <button onClick={verPerfil}>
          Ver Perfil
        </button>
      </div>

      {/* ... Otro código ... */}
    </div>
  );
}

export default VerPerfil;
